package homeworkjanuary;

public class ArithmaticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       
		 int x = 10;
		  int y = 20;
		  
		  System.out.println(x+y); //addition of number
		  
		  System.out.println("Hello" + "Java"); // combine the 2   strings
		  
		  System.out.println("Substraction is " + (y-x));
		  
		  System.out.println("Multiplication  is " + (x*y));
		  
		  System.out.println("Division is " + (y/x));
		  
		  
		   
		   System.out.println(x);
		   System.out.println(y);
		   
		   x++; // first print value then increment 
		   y--;
		  
		   System.out.println(x);
		   System.out.println(y);
		  
		   ++x; // first increment then print value
		   --y;
		   
		   System.out.println(x);
		   System.out.println(y);
	}

}
