package january19;

public class BreakContinueEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     
		 for(int x = 1; x <= 10; x++)
		 {
			 
			 System.out.println(x);
			 
			 if(x==9)
			 {
				 break;
			 }
		
			 System.out.println("Outside the loop");
			 continue;
			 
			 
		 }
	}


}
