package january19;

public class EvenOddEX {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 10;
		
		System.out.println("The Number is  " + x);
		
		if(x % 2 == 0)
		{
			
			System.out.println("The Number is Even");
			
		}
        
		else
		{
			
			System.out.println("The Number is Odd");
		}
	}

}
