package january19;

public class GreaterNumberEX {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int x = 16;
		 
		int y = 18;
		
		int z = 13;
		
		if(x>y && x>z)
		{
		 System.out.println( "x Greatest Number");
		 
		}
       
		if(x<y && y>z)
		{
		 System.out.println( " y Greatest Number");
		 
		}
		
		if(x<z && y<z)
		{
		 System.out.println( " z Greatest Number");
		 
		
		}

	}
	
	

}
