package january19;

public class SwitchEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int day = 5;
		
     
		switch(day)
		{
			
			case 1: System.out.println("First Day of week is  Sunday");
					break;
					
			case 2: System.out.println("Second Day of week is  Monday");
					break;

			case 3: System.out.println("Thrid Day of week is  Tuesday");
					break;

			case 4: System.out.println("Fourth Day of week is  Wednesday");
					break;
			
			case 5: System.out.println("Fifth Day of week is  Thursday");
					break;
			
			case 6: System.out.println("Sisxth Day of week is  Friday");
					break;
			
			case 7: System.out.println("Seventh Day of week is  Saterday");
					break;

					
			default:System.out.println("Invalid input"); 

			
			
		}
		
	}

}
